t.script:
var channel = pusher.subscribe('ocentag_bid_channel');
channel.bind('bidevent', function(data) {
    showMessageCustom(data.message);
    refreshBidColumn(data.product_id, data.bid_amount, data.product_bid_count);
    if (data.is_extend_time != 'false')
    {
        reInitTimer(data.product_id, data.extend_time);
    }
});

